<script>
    $(document).ready(function () {
        $('._js-dropdown-toggle').on('click', function (e) {
            e.preventDefault();

            // Toggle the current dropdown menu
            $(this).next('._js-dropdown-menu').slideToggle();

            // Hide other open dropdowns within the same parent to ensure only one open at a time
            $(this).parent('._js-dropdown').siblings('._js-dropdown').find('._js-dropdown-menu').slideUp();
        });
    });

    function menuToggleFun(id) {
        $(id).toggleClass('_js-active-mobile-menu');
    }

    function popupModalFun(id) {
        $(id).toggleClass('_js-active-popup');
    }
</script>

